insert-skill-stats:
{{#hasStats}}<br>
{{#manaCost}}
<br><span style="padding-left:1.5em;color:#4be;background:url(http://i1.ruliweb.com/img/18/07/07/164741ab46719dc2c.png) .15em/1em no-repeat"><b>마나</b> {{manaCost}}</span>
{{/manaCost}}
{{#manaCostPerSecond}}
<br><span style="padding-left:1.5em;color:#4be;background:url(http://i1.ruliweb.com/img/18/07/07/164741ab46719dc2c.png) .15em/1em no-repeat"><b>마나</b> 초당 {{manaCostPerSecond}}</span>
{{/manaCostPerSecond}}
{{#lifeCost}}
<br><span style="padding-left:1.5em;color:#5d2;background:url(http://i1.ruliweb.com/img/18/07/07/164741ab38d19dc2c.png) .15em/1em no-repeat"><b>생명력</b> {{lifeCost}}</span>
{{/lifeCost}}
{{#energyCost}}
<br><span style="padding-left:1.5em;color:#ee3;background:url(http://i1.ruliweb.com/img/18/07/07/164741ab5f219dc2c.png) .15em/1em no-repeat"><b>{{energyCostName}}</b> {{energyCost}}</span>
{{/energyCost}}
{{#cooldown}}
<br><span style="padding-left:1.5em;color:#ddd;background:url(http://i2.ruliweb.com/img/18/07/23/164c2a1709319dc2c.png) 0/1.3em no-repeat"><b>재사용 대기시간</b> {{cooldown}}초</span>
{{/cooldown}}
{{#rechargeCooldown}}
<br><span style="padding-left:1.5em;color:#ddd;background:url(http://i2.ruliweb.com/img/18/07/23/164c2a1709319dc2c.png) 0/1.3em no-repeat"><b>충전 대기시간</b> {{rechargeCooldown}}초</span>
{{/rechargeCooldown}}
{{#cooldownPerAlly}}
<br><span style="padding-left:1.5em;color:#ddd;background:url(http://i2.ruliweb.com/img/18/07/23/164c2a1709319dc2c.png) 0/1.3em no-repeat"><b>재사용 대기시간</b> 각 아군당 {{cooldownPerAlly}}초</span>
{{/cooldownPerAlly}}
{{#extras.length}}
<br><span style="padding-left:1.5em;color:#d90;background:url(http://i3.ruliweb.com/img/18/07/07/164748d2b0519dc2c.png) 0/1.3em no-repeat">{{#extras}}<b>{{name}}</b> {{value}} {{/extras}}</span>
{{/extras.length}}
{{/hasStats}}

insert-skill:
{{#isTalent}}
<details data-ruliweb-hots-version="{{appVersion}}" class="ruliweb-hots-talent-table" style="display:inline-block;max-WIDTH:100%;box-sizing:border-box;font-family:sans-serif;text-align:initial;padding:4px;border:2px solid #55d;background:linear-gradient(#118,#003) 0;color:#bef">
{{/isTalent}}
{{^isTalent}}
{{#isForHeroTable}}
<details style="display:inline-block;max-WIDTH:100%;box-sizing:border-box;font-family:sans-serif;text-align:initial;border:2px solid #85d;padding:4px;background:linear-gradient(#214,#102) 0;color:#6cf;margin:0 0 5px 5px;border-image:linear-gradient(#8df,#ccd) 1">
{{/isForHeroTable}}
{{^isForHeroTable}}
<details data-ruliweb-hots-version="{{appVersion}}" class="ruliweb-hots-skill-table" style="display:inline-block;max-WIDTH:100%;box-sizing:border-box;font-family:sans-serif;text-align:initial;border:2px solid #85d;padding:4px;background:linear-gradient(#214,#102) 0;color:#6cf">
{{/isForHeroTable}}
{{/isTalent}}
  <summary style="float:left;WIDTH:{{iconSize}}px;HEIGHT:{{iconSize}}px;background:url({{&iconUrl}}) center/cover" title="[클릭하여 접기/펼치기] {{#isTalent}}{{name}} ({{typeNameLong}} - 레벨 {{level}}){{/isTalent}}{{^isTalent}}{{typeName}} - {{name}}{{/isTalent}}"></summary>
  <div style="float:left;WIDTH:50em;padding-left:8px;max-WIDTH:calc(100% - {{iconSize}}px - 8px)">
    <div style="display:flex;align-items:flex-start;margin-bottom:.5em">
      <div style="flex:auto;padding-right:.5em">
        <b style="font-size:1.1em">{{name}}</b>
        {{^isForHeroTable}}<br><small>{{heroName}}{{#isTalent}} - 레벨 {{level}}{{/isTalent}}</small>{{/isForHeroTable}}
      </div>
      {{#isTypeClassBasic}}  <div style="flex:0 9 auto;border:.1em solid #87d;border-radius:.3em;padding:.2em .4em;word-break:keep-all;color:#87d">                                        {{/isTypeClassBasic}}
      {{#isTypeClassHeroic}} <div style="flex:0 9 auto;border-radius:.3em;padding:.2em .4em;word-break:keep-all;border:.1em solid #da7;color:#da7"> {{/isTypeClassHeroic}}
      {{#isTypeClassPassive}}<div style="flex:0 9 auto;border-radius:.3em;padding:.2em .4em;word-break:keep-all;border:.1em solid #6ba;color:#6ba">{{/isTypeClassPassive}}
      {{#isTypeClassActive}} <div style="flex:0 9 auto;border-radius:.3em;padding:.2em .4em;word-break:keep-all;border:.1em solid #d7a;color:#d7a"> {{/isTypeClassActive}}
        {{#upgradeFor}}
        <img alt="능력 강화" title="능력 강화" src="{{#isTypeClassBasic}}http://i1.ruliweb.com/img/18/08/07/1651405095119dc2c.png{{/isTypeClassBasic}}{{#isTypeClassHeroic}}http://i2.ruliweb.com/img/18/08/07/1651405069c19dc2c.png{{/isTypeClassHeroic}}{{#isTypeClassPassive}}http://i1.ruliweb.com/img/18/08/07/1651405088419dc2c.png{{/isTypeClassPassive}}{{#isTypeClassActive}}http://i3.ruliweb.com/img/18/08/11/165246b734f19dc2c.png{{/isTypeClassActive}}" style="HEIGHT:1.5em;margin:-1em -.2em 0 -.7em;vertical-align:bottom;max-WIDTH:none">
        {{/upgradeFor}}
        <b style="display:inline-block">{{typeName}}</b>
      {{#isTypeClassBasic}}  </div>{{/isTypeClassBasic}}
      {{#isTypeClassHeroic}} </div>{{/isTypeClassHeroic}}
      {{#isTypeClassPassive}}</div>{{/isTypeClassPassive}}
      {{#isTypeClassActive}} </div>{{/isTypeClassActive}}
    </div>
    <div style="color:#bcc">
      {{#isSimpleSkillTable}}{{{shortDescription}}}{{/isSimpleSkillTable}}
      {{^isSimpleSkillTable}}{{{description}}}{{> stats}}{{/isSimpleSkillTable}}
    </div>
    {{#hotsVersion}}<footer style="text-align:right;font-size:.8em;color:#cca"><i>패치 {{hotsVersion}}</i></footer>{{/hotsVersion}}
  </div>
{{#isTalent}}
</details>
{{/isTalent}}
{{^isTalent}}
{{#isForHeroTable}}
</details>
{{/isForHeroTable}}
{{^isForHeroTable}}
</details>
{{/isForHeroTable}}
{{/isTalent}}
